import React, { useState } from 'react';
import './Slider.css'; // Make sure to create a Slider.css file in the same directory
import img from './img1.jpg'
import img2 from './img2.jpg'
import img3 from './img3.jpeg'


const Slider = () => {
  // Define the array of image URLs directly within the component
  const images = [
     img,
     img2,
     img3,
    // Add as many images as you like
  ];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [fade, setFade] = useState(true);

  const goToIndex = (index) => {
    setFade(false);
    setTimeout(() => {
      setCurrentIndex(index);
      setFade(true);
    }, 500); // This should match the duration of the fade effect
  };

  return (
    <div className="slider">
      <div className={`slider-images ${fade ? 'fade' : ''}`}>
        <img src={images[currentIndex]} alt={`Slide ${currentIndex}`} />
      </div>
      <div className="slider-pagination">
        {images.map((_, index) => (
          <button
            key={index}
            className={`dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => goToIndex(index)}
          ></button>
        ))}
      </div>
    </div>
  );
};

export default Slider;
